mapper.select:
select b.name as name, b.drug_specification as drugSpecification, b.measure as measure, b.frequency as frequency, h.building_name AS buildingName, h.floor_name AS floorName, h.room_name AS roomName, h.bed_name AS bedName from bus_medication_record b left join bus_hospital_record h on b.business_no = h.business_no where b.is_del =0 AND h.is_del = 0 AND h.status = '0'
AND b.create_time >= to_timestamp(#{startTime}, 'yyyy-MM-dd hh24:mi:ss')
AND b.create_time <= to_timestamp(#{endTime}, 'yyyy-MM-dd hh24:mi:ss')
and h.building_code = #{buildingCode}
and h.floor_code = #{floorCode}
and b.business_no = #{businessNo}
ORDER BY h.building_code desc, h.floor_code desc, h.bed_code desc, b.medication_date asc

select t1.id AS id, DATE_FORMAT(t1.medication_date,'%Y-%m-%d %H:%i:%s') AS medicationDate, t1.bed_code AS bedCode, t1.name AS name, t1.drug_specification AS drugSpecification, t1.measure AS measure, t1.frequency AS frequency, t1.medication_time AS medicationTime, t1.business_no AS businessNo, t1.ward AS ward from bus_medication_record t1 left join bus_hospital_record t2 on t1.business_no = t2.business_no where t1.is_del =0 AND t2.is_del = 0 AND t2.status = '0'
and t2.building_code = #{buildingCode}
and t2.floor_code = #{floorCode}
and t1.business_no = #{businessNo}
and t1.name like concat('%',#{name},'%')
ORDER BY t2.building_code desc, t2.floor_code desc, t2.bed_code desc, t1.medication_date asc
